void ui_event_zhuye5(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);

    if(event_code == LV_EVENT_CLICKED) {
        _ui_flag_modify(ui_zhuye5, LV_OBJ_FLAG_HIDDEN, _UI_MODIFY_FLAG_ADD);
        
        // 停止并清理音乐播放器
        stop_music();
        system("rm -f /tmp/mplayer_fifo");
        
        // 释放音乐列表内存
        if (music_list) {
            for (int i = 0; i < music_count; i++) {
                free(music_list[i]);
            }
            free(music_list);
            music_list = NULL;
            music_count = 0;
        }
    }
}




