    ui_zhuye5 = NULL;
    ui_sound5 = NULL;
    ui_WIFI5 = NULL;
    ui_blueteeth5 = NULL;
    ui_Slider6 = NULL;
    ui_stopmusic = NULL;
    ui_playmusic = NULL;
    ui_backmusic = NULL;
